import React, { Component } from 'react';
import axios from 'axios';
import classnames from 'classnames';

class Registration extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      password: '',
      password2: '',
      errors: {}
    };

  this.onChange = this.onChange.bind(this);
  this.onSubmit = this.onSubmit.bind(this);
}

onChange(event) {
  this.setState({ [event.target.name]: event.target.value });
}

// Prevents Reload
onSubmit(event) {
  event.preventDefault();
}


  render() {
    return (

      <form>
        <div class="form-group">
          <label for="registration_email">ðŸ“¤ Email address ðŸ“¤</label>
          <input type="email" class="form-control" id="registration_email" aria-describedby="emailHelp" placeholder="Enter email"></input>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="registration_name">Name</label>
          <input type="text" class="form-control" id="registration_name" aria-describedby="passwordHelp" placeholder="What's your name?"></input>
        </div>
        <div class="form-group">
          <label for="registration_password">Password</label>
          <input type="password" class="form-control" id="registration_password" aria-describedby="passwordHelp" placeholder="Please create a password between 3-30 characters"></input>
        </div>
        <div class="form-group">
          <label for="registration_password_confirm">ðŸ”‘ Confirm Password ðŸ”‘</label>
          <input type="password" class="form-control" id="registration_password_confirm" aria-describedby="password_confirmHelp" placeholder="Please Repeat your new Password"></input>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    )
  }
}

export default Registration;
