import React, { Component } from 'react';
import axios from 'axios';

class PostQuestion extends Component {
  constructor() {
    super();
    this.state = {
      name: localStorage.getItem('name'),
      question: '',
      tags: [],
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    axios
      .post('/api/posts/')
      .then(res => { 
        this.setState({posts: res.data})
        console.log(res.data);
       })
      .catch(err => this.setState({ errors: err.response.data }));
  }

        render() {
    const { errors } = this.state;

    return (
      <div className="question">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Post an Interview Question!</h1>
              <p className="lead text-center">
                Create your FullQuack Account
              </p>
              <form noValidate onSubmit={this.onSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames('form-control form-control-lg', {
                      'is-invalid': errors.question
                    })}
                    placeholder="Quacked Question"
                    name="question"
                    value={this.state.question}
                    onChange={this.onChange}
                  />
                  {errors.name && (
                    <div className="invalid-feedback">{errors.question}</div>
                  )}
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Tags (i.e., Javascript, Google Interview, React Dev)"
                    name="tags"
                    value={this.state.tags}
                    onChange={this.onChange}
                  />
                </div>
                <input type="submit" className="btn btn-info btn-block mt-4" />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}


export default PostQuestion;
