import React, { Component } from 'react';
import axios from 'axios';
import classnames from 'classnames';

class Registration extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      password: '',
      password2: '',
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });

  }

  onSubmit(e) {
    e.preventDefault();

    const newRegisteredUser = {
      name: this.state.name,
      email: this.state.email,
      password: this.state.passwod,
      password2: this.state.password2
    };

    axios
      .post('/api/users/register', newRegisteredUser)
      .then(res => console.log(res.data))
      .catch(err => this.setState({ errors: err.response.data }));
  }

  render() {
    const { errors } = this.state;
    return (
      <div className="registration">
        <h1 className="display-3 text-center">Let's Get Quackin'</h1>
        <form noValidate onSubmit={this.onSubmit}>
          <div className="form-group">
            <input
              type="text"
              className={classnames('form-control form-control-lg', {
                'is-invalid': errors.name
              })}
              placeholder="ðŸ‘‹ Name ðŸ‘‹"
              name="name"
              value={this.state.name}
              onChange={this.onChange}/>
            {errors.name && (
              <div className="invalid-feedback">{errors.name}</div>
            )}
            </div>
            <input type="submit" className="btn btn-light btn-block mt-2"/>
            </form>
      </div>

    )
  }
}

export default Registration;
