import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';


class SuccessLogin extends Component {
  constructor() {
    super();
    this.state = {
      name: localStorage.getItem('name'),
      posts: [],
    };
  }

  componentDidMount() {
    axios
      .get('/api/posts/')
      .then(res => { 
        this.setState({posts: res.data})
        console.log(res.data);
       })
      .catch(err => this.setState({ errors: err.response.data }));
  }

  render() {
    let allPosts = [];
    for (let i = 0; i < this.state.posts.length; i++) {
      allPosts.push(<span className='question' key={i}>{this.state.posts[i].text}<hr></hr></span>);
    }
    return (
      <div className="successLogin-container">
        <h2>{this.state.name} successfully Logged in!</h2>
        <Link to="/postquestion" className = "btn btn-lg btn-light">
          Quack a new Question!
        </Link>
        <div>
        {allPosts}
        </div>
      </div>

    )
  }
}

export default SuccessLogin;
