import React, { Component } from 'react';
import axios from 'axios';
import classnames from 'classnames';

class Registration extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      password: '',
      password2: '',
      errors: {}
    };

  this.onChange = this.onChange.bind(this);
  this.onSubmit = this.onSubmit.bind(this);
}

onChange(event) {
  this.setState({ [event.target.name]: event.target.value });
}

onSubmit(event) {
  // Prevents Reload
  event.preventDefault();

  // Take in the Submitted Registration data
  const newRegisteredUser = {
    name: this.state.name,
    email: this.state.email,
    password: this.state.password,
    password2: this.state.password2
  };

  axios
    .post('/api/users/register', newRegisteredUser)
    .then(res => console.log(res.data))
    .catch(err => this.setState({ errors: err.response.data }));
}



  render() {
    const { errors } = this.state;

    return (

      <form noValidate onSubmit={this.onSubmit}>
        <div class="form-group">
          <label for="registration_email">📤 Email address 📤</label>
          <input type="email" id="registration_email" aria-describedby="emailHelp" className={classnames('form-control form-control-sm', {'is-invalid': errors.email})} placeholder="Enter email" value={this.state.email} onChange={this.onChange}></input>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="registration_name">Name</label>
          <input type="text" id="registration_name" aria-describedby="passwordHelp" className={classnames('form-control form-control-sm', {'is-invalid': errors.name})} value={this.state.name} onChange={this.onChange} placeholder="What's your name?"></input>
        </div>
        <div class="form-group">
          <label for="registration_password">Password</label>
          <input type="password" id="registration_password" aria-describedby="passwordHelp" className={classnames('form-control form-control-sm', {'is-invalid': errors.password})} value={this.state.password} onChange={this.onChange} placeholder="Please create a password between 3-30 characters"></input>
        </div>
        <div class="form-group">
          <label for="registration_password_confirm">🔑 Confirm Password 🔑</label>
          <input type="password" className={classnames('form-control form-control-sm', {'is-invalid': errors.password2})}id="registration_password_confirm" aria-describedby="password_confirmHelp" value={this.state.password2} onChange={this.onChange} placeholder="Please Repeat your new Password"></input>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    )
  }
}

export default Registration;
